use std::collections::HashSet;


trait MarkerFinder {
    fn find_marker_of_size(&self, size: usize) -> Option<usize>;
}

impl MarkerFinder for str {
    fn find_marker_of_size(&self, size: usize) -> Option<usize> {
        let mut sliding_window: Vec<char> = self.chars().take(size).collect();
        
        for (i, c) in self.chars().enumerate().skip(size) {
            // convert a vec to a hash set
            let set: HashSet<char> = sliding_window.iter().cloned().collect();
            if set.len() == size {
                return Some(i);
            }
            sliding_window.remove(0);
            sliding_window.push(c);
        }
        None
    }
}
fn main() {
    let file = "";
    // create a hash set from the first 4 chars in the file variable
    println!("{}", file.find_marker_of_size(4).unwrap());
    println!("{}", file.find_marker_of_size(14).unwrap());
}
